patterns.template:
{{# field }}
<div class="form-group">
	<label for="{{ id }}" class="c-field__label">{{ label }}</label>
	<input id="{{ id }}" type="{{ type }}" placeholder="{{ placeholder }}" value="{{ value }}" />
	{{# fieldNote }}
		<span class="c-field__note">{{{ fieldNote }}}</span>
	{{/ fieldNote }}

</div><!--end c-field-->
{{/ field }}

